import { Container, Row, Col, Card, Button } from "react-bootstrap";
import 'animate.css';
import TrackVisibility from 'react-on-screen';
import myPicture from "../assets/img/my-picture.png"; // Replace with your image path

export const About = () => {
  const handleConnectClick = () => {
    const connectSection = document.getElementById("connect");
    if (connectSection) {
      connectSection.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <section className="about" id="about">
      <Container>
        <Row className="align-items-center">
          <Col md={6}>
            <div className="picture-frame">
              <img
                src={myPicture}
                alt="My Portrait"
                className="profile-picture"
              />
            </div>
          </Col>
          <Col md={6}>
            <TrackVisibility>
              {({ isVisible }) =>
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2>About Me</h2>
                  <p>
                    Hi, I’m Truong! I’m passionate about uncovering insights from data and building impactful solutions using machine learning and data visualization.
                  </p>
                  <Card className="education-card">
                    <Card.Body>
                      <Card.Title>Education</Card.Title>
                      <Card.Text>
                        <strong>San Jose State University</strong> <br />
                        Bachelor of Science in Data Science
                      </Card.Text>
                    </Card.Body>
                  </Card>
                  <Button variant="primary" onClick={handleConnectClick}>
                    Let’s Connect
                  </Button>
                </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
