import { Container, Row, Col } from "react-bootstrap";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const About = () => {
  return (
    <section className="about" id="about">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2>About Me</h2>
                  <p>
                    Hi, my name is Truong, and I’m an aspiring Data Scientist and Data Analyst passionate about uncovering insights from data. 
                    I specialize in leveraging machine learning, statistical modeling, and data visualization techniques to solve real-world problems.
                  </p>
                  <p>
                    With experience in Python, SQL, and data wrangling, I have worked on various projects ranging from business analytics to predictive modeling. 
                    I thrive on collaborating with teams to deliver impactful solutions, and I am committed to continuous learning and improvement.
                  </p>
                  <p>
                    When I’m not coding, you can find me exploring new technologies, fixing electronics, or working on creative side projects.
                  </p>
                </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
